$(".nav__item").hover(onIn, onOut);
$(".nav__show").click(function() {
    if ((window.innerWidth <= 1200)) {
        $(this).parents(".nav__item").toggleClass("nav__item--active")
        $(this).siblings(".nav__hidden").slideToggle()
    }
})


function onIn() {
    if (window.innerWidth > 1200) {
        let el = $(this)
        setTimeout(function() {
            if (el.is(':hover')) {
                el.addClass("nav__item--active")
            }
        }, 200);
    }
}

function onOut() {
    if (window.innerWidth > 1200) {
        $(this).removeClass("nav__item--active")
    }
}


$(".burger").click(function () {
    $("body").toggleClass("fixed-body")
    $(".header").toggleClass("header--active")
})

let SlidersArray = []

function Sliders() {
    $(".slider").each(function(index, el) {
        $(el).attr("data-slider-id", `${index}`)
        SlidersArray[index] = new Swiper(`.slider[data-slider-id="${index}"] .swiper-container`, {
            slidesPerView: "auto",
            spaceBetween: 30,
            autoplay: {
              delay: 3000
            },
            scrollbar: {
                el: `.slider[data-slider-id="${index}"] .swiper-scrollbar`,
                draggable: true,
            },
            breakpoints: {
                320:{
                    spaceBetween: 15,
                },
                576:{
                    spaceBetween: 30,
                }
            }
        })
    })
}

function gallery() {
    var swiper = new Swiper(".gallery .mySwiper", {
        spaceBetween: 18,
        slidesPerView: 3,
        freeMode: true,
        watchSlidesProgress: true,
        navigation: {
            nextEl: '.gallery .swiper-button-next',
            prevEl: '.gallery .swiper-button-prev',
        },
        autoplay: {
            delay: 3000
        },
        breakpoints: {
            320:{
                spaceBetween: 12,
                slidesPerView: 2,
            },
            576:{
                spaceBetween: 18,
                slidesPerView: 3,
            }
        }
    });
    var swiper2 = new Swiper(".gallery .mySwiper2", {
        spaceBetween: 10,
        thumbs: {
            swiper: swiper,
        },
        autoplay: {
            delay: 3000
        },
    });
}



